    },
    incrementEggMeat() {
      this.eggMeatCount++
    },
    decrementEggMeat() {
      if (this.eggMeatCount > 0) {
        this.eggMeatCount--
      }
    },
    async checkAndSubmit() {
      if (this.eggMeatCount + this.meatCount == 0) {
          this.errMessage = '请输入正确的数量'
      } else if (this.name == null ) {
        this.errMessage = '姓名必须填写'
      } else if (this.contact == null) {
        this.errMessage = '联系电话必须填写'
      } else if (this.address == null) {
        this.errMessage = '地址必须填写'
      } else {
        let res = await axios.post("http://106.15.104.76:3001/orders/create",{
          "contact": this.contact,
          "address": this.address,
          "name": this.name,
          "meat": this.meatCount,
          "eggAndMeat": this.eggMeatCount,
        })
        if (res.data.success == true) {
          this.errMessage = null;
          window.location.href = 'docs';
          // 弹框，创建订单成功
        } else if (res.data.data.message == 'DUPLICATED ORDERS') {
          this.errMessage = '对不起，您已经提交过，请勿重复提交'
        }
      }
    },
  }
}
</script>
